const message = $json.message.text;
const chatId = $json.message.chat.id;
const telegramUser = $json.message.from;

const telegramUserId = telegramUser.id;
const telegramUsername = telegramUser.username || null;
const telegramFirstName = telegramUser.first_name || '';
const telegramLastName = telegramUser.last_name || '';

if (!message || message.trim() === '') {
  return [{
    json: {
      error: true,
      chatId: chatId,
      message: "Aktivitas tidak boleh kosong!\n\nSilakan tulis aktivitas Anda\nContoh: Mengerjakan laporan harian\n\nKetik /start untuk memulai."
    }
  }];
}

const aktivitas = message.trim();

if (aktivitas.length < 5) {
  return [{
    json: {
      error: true,
      chatId: chatId,
      message: "Aktivitas terlalu singkat!\n\nMinimal 5 karakter untuk aktivitas\nContoh: Mengerjakan laporan harian\n\nKetik /start untuk memulai ulang."
    }
  }];
}

if (aktivitas.length > 200) {
  return [{
    json: {
      error: true,
      chatId: chatId,
      message: "Aktivitas terlalu panjang!\n\nMaksimal 200 karakter untuk aktivitas\n\nKetik /start untuk memulai ulang."
    }
  }];
}

return [{
  json: {
    error: false,
    aktivitas: aktivitas,
    chatId: chatId,
    telegramUser: {
      userId: telegramUserId,
      username: telegramUsername,
      firstName: telegramFirstName,
      lastName: telegramLastName,
      fullName: `${telegramFirstName} ${telegramLastName}`.trim()
    }
  }
}];

