const employees = $('Get Employees').all();

const chatIdFields = ['IDtelegram'];

// ✅ FIX 2: DEKLARASIKAN VARIABLE INI!
let employeeByChatId = null;

for (const emp of employees) {
    for (const chatField of chatIdFields) {
        if (emp.json[chatField] && String(emp.json[chatField]).trim() === String(chatId).trim()) {
            employeeByChatId = emp;
            break;
        }
    }
    if (employeeByChatId) break;
}

const chatIdFound = !!employeeByChatId;

if (!chatIdFound) {
    return [{
        json: {
            found: false,
            error: 'chat_id_not_found',
            chatId: chatId,
            telegramUser: telegramUser,
            aktivitas: aktivitas,
            chatid: chatIdFound
        }
    }];
}

const employee = employeeByChatId.json;
const formattedData = {
    found: true,
    error: null,
    chatId: chatId,
    chatID: chatId,
    telegramUser: telegramUser,
    employee: employee,
    aktivitas: aktivitas,
    Nip: employee.Nip || employee.NIP || employee.nip,
    Nama: employee.Nama || employee.nama || employee.NAMA,
    Aktivitas: aktivitas,
    Tanggal: new Date().toLocaleString('id-ID', {
        timeZone: 'Asia/Jakarta',
        weekday: 'long', 
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
    }).replace('pukul','').trim(),
    verificationStatus: {
        chatIdVerified: true,
        verificationMethod: 'chat_id'
    }
};

return [{ json: formattedData }];

